mapper.select:
select board_id, group_id, user_id, title, content, view_cnt, reg_dtm, reg_user
from board where group_id=#{groupId}
order by reg_dtm desc;

SELECT r1.* FROM (
	select board_id, group_id, user_id, title, content, view_cnt, reg_dtm, reg_user
	from board where group_id=#{groupId}
	order by reg_dtm desc) r1
limit #{startPage}, #{recordsPerPage}

SELECT r1.* FROM (
	select board_id, group_id, user_id, title, content, view_cnt, reg_dtm
	from board where group_id=#{groupId}

		and title like concat('%',#{keyword},'%')

	order by reg_dtm desc) r1
limit #{skip}, #{recordsPerPage}

SELECT r1.* FROM (
	select board_id, group_id, user_id, title, content, view_cnt, reg_dtm, reg_user
	from board where group_id=#{groupId} and (title like concat('%',#{keyword},'%') or content like concat('%',#{keyword},'%'))
	order by reg_dtm desc) r1
limit #{skip}, #{recordsPerPage}

SELECT r1.* FROM (
	select board_id, group_id, user_id, title, content, view_cnt, reg_dtm, reg_user
	from board where group_id=#{groupId} and reg_user like concat('%',#{keyword},'%')
	order by reg_dtm desc) r1
limit #{skip}, #{recordsPerPage}

mapper.update:
update board
set title=#{title}, content=#{content}
where board_id=#{boardId}

update board
set view_cnt=view_cnt+1
where board_id=#{boardId}
