mapper.select:
select group_id, lec_id, group_name, school_name, short_dsc, long_dsc, max_mem, create_date, reg_user, cur_mem
from study_group
where lec_id=#{lecId}

select group_id, lec_id, group_name, school_name, short_dsc, long_dsc, max_mem, create_date, reg_user, cur_mem
from study_group
where group_id=#{groupId}

mapper.update:
update study_group
set cur_mem = cur_mem+1
where group_id=#{groupId}
